import React, { useEffect, useRef, useCallback } from 'react';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import Brick from './entities/Brick'; // Import Brick
import { useGameLoop } from './GameLoop';
import LevelManager from './LevelManager';
import PauseMenu from '../UI/PauseMenu';
import GameOver from '../UI/GameOver';
import SoundManager from '../../utils/SoundManager';
import '../../styles/components/GameCanvas.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const gameObjectsRef = useRef(null);
  const requestIdRef = useRef({ id: null, lastTime: null });
  const levelManagerRef = useRef(new LevelManager());

  const {
    isPaused,
    isGameOver,
    setScore,
    score,
    lives,
    setLives,
    currentLevel,
    setIsPaused,
    setIsGameOver,
    setIsVictory,
    isVictory,
    stop,
    setCurrentLevel
  } = useGameLoop();

  const drawHUD = useCallback((ctx) => {
    ctx.save();
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '20px Arial';

    // Draw score
    ctx.textAlign = 'left';
    ctx.fillText(`Score: ${score}`, 10, 30);

    // Draw level
    ctx.textAlign = 'center';
    ctx.fillText(`Level ${currentLevel}`, canvasRef.current.width / 2, 30);

    // Draw lives
    ctx.textAlign = 'right';
    ctx.fillText(`Lives: ${lives}`, canvasRef.current.width - 10, 30);
    ctx.restore();
  }, [score, lives, currentLevel]);

  const resetBallAndPaddle = useCallback(() => {
    if (!gameObjectsRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const paddleY = canvas.height - 40;
    const paddleWidth = 100;

    // Reset paddle
    const paddle = gameObjectsRef.current.paddle;
    paddle.x = canvas.width / 2 - paddleWidth / 2;
    paddle.y = paddleY;
    paddle.dx = 0;
    paddle.leftPressed = false;
    paddle.rightPressed = false;

    // Reset ball
    const ball = gameObjectsRef.current.ball;
    ball.x = canvas.width / 2;
    ball.y = paddleY - ball.radius;
    ball.dx = 0;
    ball.dy = 0;
    ball.isLaunched = false;
  }, []);

  const handleBallOut = useCallback((objects) => {
    const newLives = lives - 1;
    setLives(newLives);

    if (newLives <= 0) {
      setIsGameOver(true);
      setIsVictory(false);
      stop();
    } else {
      resetBallAndPaddle();
    }
  }, [lives, setLives, setIsGameOver, setIsVictory, stop, resetBallAndPaddle]);

  const resetLevel = useCallback(() => {
    if (!gameObjectsRef.current) return;

    // Get fresh level data for the current level
    const levelData = levelManagerRef.current.loadLevel(currentLevel);

    // Create new Brick instances to ensure clean state
    gameObjectsRef.current.bricks = levelData.bricks.map(brick => {
      return new Brick(brick.x, brick.y, brick.width, brick.height, brick.type);
    });
  }, [currentLevel]);

  const drawGameObjects = useCallback((ctx, objects) => {
    if (!ctx || !objects) return;

    ctx.save();
    objects.bricks.forEach(brick => {
      if (!brick.isDestroyed()) {
        brick.draw(ctx);
      }
    });
    objects.ball.draw(ctx);
    objects.paddle.draw(ctx);
    ctx.restore();
  }, []);

  const handleKeyDown = useCallback((e) => {
    if (isGameOver) return;

    const objects = gameObjectsRef.current;
    switch (e.key.toLowerCase()) {
      case 'a':
      case 'arrowleft':
        if (!isPaused) objects.paddle.moveLeft();
        break;
      case 'd':
      case 'arrowright':
        if (!isPaused) objects.paddle.moveRight();
        break;
      case ' ':
        if (!isPaused && objects.ball) objects.ball.launch();
        break;
      case 'escape':
        setIsPaused(prev => !prev);
        break;
      default:
        break;
    }
  }, [isPaused, isGameOver, setIsPaused]);

  const handleKeyUp = useCallback((e) => {
    if (isGameOver || isPaused) return;

    const objects = gameObjectsRef.current;
    switch (e.key.toLowerCase()) {
      case 'a':
      case 'arrowleft':
        objects.paddle.stopLeft();
        break;
      case 'd':
      case 'arrowright':
        objects.paddle.stopRight();
        break;
      default:
        break;
    }
  }, [isPaused, isGameOver]);

  const handleResume = useCallback(() => {
    setIsPaused(false);
  }, [setIsPaused]);

  const handleRestart = useCallback(() => {
    // Reset game state
    setIsPaused(false);
    setIsGameOver(false);
    setIsVictory(false);
    setScore(0);
    setLives(3);
    setCurrentLevel(1);

    // Reset level manager to ensure fresh state
    levelManagerRef.current = new LevelManager();

    // Reset game objects and level
    resetLevel();
    resetBallAndPaddle();

    // Ensure ball is not launched
    if (gameObjectsRef.current) {
      gameObjectsRef.current.ball.isLaunched = false;
    }
  }, [setIsPaused, setIsGameOver, setIsVictory, setScore, setLives, setCurrentLevel, resetLevel, resetBallAndPaddle]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    canvas.width = 800;
    canvas.height = 600;

    // Only initialize game objects if they don't exist
    if (!gameObjectsRef.current) {
      const paddleY = canvas.height - 40;
      const paddleWidth = 100;

      gameObjectsRef.current = {
        paddle: new Paddle(canvas.width / 2 - paddleWidth / 2, paddleY),
        ball: new Ball(canvas.width / 2, paddleY - 10),
        bricks: []
      };

      const levelData = levelManagerRef.current.loadLevel(currentLevel);
      gameObjectsRef.current.bricks = levelData.bricks;
    }

    // Store animation ID in a variable that will be captured in the cleanup
    const requestId = requestIdRef.current;
    const animate = (timestamp) => {
      const ctx = canvasRef.current?.getContext('2d', { alpha: false });
      const objects = gameObjectsRef.current;

      if (!ctx || !objects) {
        requestId.id = requestAnimationFrame(animate);
        return;
      }

      if (!requestId.lastTime) {
        requestId.lastTime = timestamp;
        requestId.id = requestAnimationFrame(animate);
        return;
      }

      const deltaTime = Math.min((timestamp - requestId.lastTime) / 1000, 0.032);
      requestId.lastTime = timestamp;

      // Clear canvas
      ctx.fillStyle = 'rgb(0, 0, 0)';
      ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

      if (!isPaused && !isGameOver) {
        // Update paddle
        objects.paddle.update(deltaTime);

        // Handle paddle collision before updating ball
        if (objects.ball.checkPaddleCollision(objects.paddle)) {
          objects.ball.bounceOffPaddle(objects.paddle);
          SoundManager.playPaddleHit();
        }

        // Only stick to paddle if not launched
        if (!objects.ball.isLaunched) {
          objects.ball.stickToPaddle(objects.paddle);
        }

        // Update ball position
        objects.ball.update(deltaTime);

        // Check for brick collisions
        let remainingBricks = 0;

        for (const brick of objects.bricks) {
          if (!brick.isDestroyed()) {
            remainingBricks++;
            if (objects.ball.checkBrickCollision(brick)) {
              objects.ball.bounceOffBrick(brick);
              if (brick.hit()) {
                setScore(prev => prev + 100);
                SoundManager.playBrickHit();
              }
              break;
            }
          }
        }

        // Check if ball is out of bounds
        if (objects.ball.y > canvasRef.current.height) {
          handleBallOut(objects);
        }

        // Check if level is complete
        if (objects.bricks.length > 0 && remainingBricks === 0) {
          const nextLevel = currentLevel + 1;
          if (nextLevel <= levelManagerRef.current.getMaxLevel()) {
            setCurrentLevel(nextLevel);
            resetLevel();
            resetBallAndPaddle();
            // Reset ball launch state
            objects.ball.isLaunched = false;
          } else {
            setIsGameOver(true);
            setIsVictory(true);
            stop();
          }
        }
      }

      // Always draw game objects and HUD
      drawGameObjects(ctx, objects);
      drawHUD(ctx);

      requestId.id = requestAnimationFrame(animate);
    };
    requestId.id = requestAnimationFrame(animate);

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      if (requestId.id) {
        cancelAnimationFrame(requestId.id);
      }
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [handleKeyDown, handleKeyUp, currentLevel, drawGameObjects, drawHUD, handleBallOut, 
      isGameOver, isPaused, resetBallAndPaddle, resetLevel, setCurrentLevel, 
      setIsGameOver, setIsVictory, setScore, stop]);

  return (
    <div className="game-container">
      <div className="canvas-wrapper">
        <canvas
          ref={canvasRef}
          className="game-canvas"
          width={800}
          height={600}
          tabIndex={0}
        />
        {isPaused && (
          <div className="pause-overlay">
            <PauseMenu
              onResume={handleResume}
              onRestart={handleRestart}
            />
          </div>
        )}
        {isGameOver && (
          <div className="game-over-overlay">
            <GameOver
              score={score}
              level={currentLevel}
              onRestart={handleRestart}
              isVictory={isVictory}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default GameCanvas;
