    }

    if (newY - this.radius <= 0) {
      this.y = this.radius;
      this.dy = Math.abs(this.dy);
    } else {
      this.y = newY;
    }

    // Normalize speed
    const currentSpeed = Math.sqrt(this.dx * this.dx + this.dy * this.dy);
    if (Math.abs(currentSpeed - this.speed) > 0.1) {
      const speedFactor = this.speed / currentSpeed;
      this.dx *= speedFactor;
      this.dy *= speedFactor;
    }
  }

  launch() {
    if (!this.isLaunched) {
      this.isLaunched = true;
      const angle = -Math.PI / 4; // -45 degrees
      this.dx = this.speed * Math.cos(angle);
      this.dy = this.speed * Math.sin(angle);
    }
  }

  reset() {
    this.x = this.initialX;
    this.y = this.initialY;
    this.dx = 0;
    this.dy = 0;
    this.isLaunched = false;
    this.speed = this.baseSpeed;
  }

  stickToPaddle(paddle) {
    if (!this.isLaunched) {
      this.x = paddle.x + paddle.width / 2;
      this.y = paddle.y - this.radius;
    }
  }

  bounceOffPaddle(paddle) {
    if (!this.isLaunched) return;

    // Calculate relative position of ball hit on paddle
    const relativeIntersectX = (this.x - (paddle.x + paddle.width / 2)) / (paddle.width / 2);

    // Calculate bounce angle (-60 to 60 degrees)
    const bounceAngle = relativeIntersectX * Math.PI / 3;

    // Set new velocity
    this.dx = this.speed * Math.sin(bounceAngle);
    this.dy = -Math.abs(this.speed * Math.cos(bounceAngle));

    // Ensure ball is above paddle
    this.y = paddle.y - this.radius;
  }

  bounceOffBrick(brick) {
    if (!this.isLaunched) return;

    // Find the point of collision
    const ballCenterX = this.x;
    const ballCenterY = this.y;
    const brickCenterX = brick.x + brick.width / 2;
    const brickCenterY = brick.y + brick.height / 2;

    // Calculate overlap
    const dx = Math.abs(ballCenterX - brickCenterX);
    const dy = Math.abs(ballCenterY - brickCenterY);

    // Determine if collision is more horizontal or vertical
    if (dx / brick.width > dy / brick.height) {
      // Horizontal collision
      this.dx = -this.dx;
    } else {
      // Vertical collision
      this.dy = -this.dy;
    }

    // Move ball slightly away from brick to prevent sticking
    const moveDistance = this.radius * 0.1;
    if (this.dx > 0) {
      this.x += moveDistance;
    } else {
      this.x -= moveDistance;
    }
    if (this.dy > 0) {
      this.y += moveDistance;
    } else {
      this.y -= moveDistance;
    }
  }

  checkPaddleCollision(paddle) {
    return (
      this.isLaunched &&
      this.x + this.radius > paddle.x &&
      this.x - this.radius < paddle.x + paddle.width &&
      this.y + this.radius > paddle.y &&
      this.y - this.radius < paddle.y + paddle.height
    );
  }

  checkBrickCollision(brick) {
    if (!this.isLaunched || brick.isDestroyed()) return false;

    // Simple AABB collision check
    const ballLeft = this.x - this.radius;
    const ballRight = this.x + this.radius;
    const ballTop = this.y - this.radius;
    const ballBottom = this.y + this.radius;

    const brickLeft = brick.x;
    const brickRight = brick.x + brick.width;
    const brickTop = brick.y;
    const brickBottom = brick.y + brick.height;

    return (
      ballRight >= brickLeft &&
      ballLeft <= brickRight &&
      ballBottom >= brickTop &&
      ballTop <= brickBottom
    );
  }
