import React, { useRef, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import PauseMenu from '../UI/PauseMenu';
import GameOver from '../UI/GameOver';
import LevelManager from './LevelManager';
import '../../styles/components/GameCanvas.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const gameObjectsRef = useRef(null);
  const requestIdRef = useRef(null);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [currentLevel, setCurrentLevel] = useState(1);
  const [isPaused, setIsPaused] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isVictory, setIsVictory] = useState(false);

  // Create levelManager ref to avoid dependency issues
  const levelManagerRef = useRef(new LevelManager());

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = 800;
    canvas.height = 600;
    const ctx = canvas.getContext('2d');

    // Initialize game objects if not already initialized
    if (!gameObjectsRef.current) {
      gameObjectsRef.current = {
        ball: new Ball(canvas.width / 2, canvas.height - 30),
        paddle: new Paddle(canvas.width / 2 - 50, canvas.height - 20),
        bricks: levelManagerRef.current.loadLevel(currentLevel).bricks
      };
    }

    let lastTime = 0;

    // Handle keyboard input
    const handleKeyDown = (e) => {
      console.log('KeyDown:', e.key); // Debug log
      if (!isPaused && !isGameOver) {
        switch (e.key.toLowerCase()) {
          case 'a':
          case 'arrowleft':
            console.log('Moving left'); // Debug log
            if (gameObjectsRef.current?.paddle) {
              gameObjectsRef.current.paddle.moveLeft();
            }
            break;
          case 'd':
          case 'arrowright':
            console.log('Moving right'); // Debug log
            if (gameObjectsRef.current?.paddle) {
              gameObjectsRef.current.paddle.moveRight();
            }
            break;
          case ' ':
            console.log('Launch ball'); // Debug log
            if (gameObjectsRef.current?.ball && !gameObjectsRef.current.ball.isLaunched) {
              gameObjectsRef.current.ball.launch();
            }
            break;
          case 'escape':
            setIsPaused(prev => !prev);
            break;
          default:
            break;
        }
      }
    };

    const handleKeyUp = (e) => {
      console.log('KeyUp:', e.key); // Debug log
      if (!isPaused && !isGameOver) {
        switch (e.key.toLowerCase()) {
          case 'a':
          case 'arrowleft':
            console.log('Stop left'); // Debug log
            if (gameObjectsRef.current?.paddle) {
              gameObjectsRef.current.paddle.stopLeft();
            }
            break;
          case 'd':
          case 'arrowright':
            console.log('Stop right'); // Debug log
            if (gameObjectsRef.current?.paddle) {
              gameObjectsRef.current.paddle.stopRight();
            }
            break;
          default:
            break;
        }
      }
    };

    // Store canvas reference for cleanup
    const currentCanvas = canvasRef.current;
    currentCanvas.tabIndex = 0; // Make canvas focusable
    currentCanvas.focus(); // Focus the canvas
    currentCanvas.addEventListener('keydown', handleKeyDown);
    currentCanvas.addEventListener('keyup', handleKeyUp);

    // Game loop function
    const gameLoop = (timestamp) => {
      if (!lastTime) {
        lastTime = timestamp;
      }

      const currentTime = timestamp;
      lastTime = currentTime;

      if (!isPaused && !isGameOver) {
        const objects = gameObjectsRef.current;

        // Clear canvas
        ctx.fillStyle = '#000000';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        // Update game objects
        objects.paddle.update();

        // Make ball stick to paddle if not launched
        if (!objects.ball.isLaunched) {
          objects.ball.stickToPaddle(objects.paddle);
        } else {
          objects.ball.update();
        }

        // Draw game objects in correct order
        // 1. Draw bricks
        objects.bricks.forEach(brick => {
          if (!brick.isDestroyed()) {
            brick.draw(ctx);
          }
        });

        // 2. Draw ball
        objects.ball.draw(ctx);

        // 3. Draw paddle
        objects.paddle.draw(ctx);

        // Draw debug info
        ctx.font = '12px Courier New';
        ctx.fillStyle = '#00FF00';
        ctx.textAlign = 'left';
        ctx.fillText(`Ball: x=${Math.round(objects.ball.x)}, y=${Math.round(objects.ball.y)}, launched=${objects.ball.isLaunched}`, 10, canvas.height - 60);
        ctx.fillText(`Paddle: x=${Math.round(objects.paddle.x)}, y=${Math.round(objects.paddle.y)}, left=${objects.paddle.leftPressed}, right=${objects.paddle.rightPressed}`, 10, canvas.height - 40);

        // Draw HUD
        ctx.font = '16px Courier New';
        ctx.fillStyle = '#00FF00';

        // Score on the left
        ctx.textAlign = 'left';
        ctx.fillText(`Score: ${score}`, 10, 40);

        // Level in the center
        ctx.textAlign = 'center';
        ctx.fillText(`Level: ${currentLevel}`, canvas.width / 2, 40);

        // Lives on the right
        ctx.textAlign = 'right';
        ctx.fillText(`Lives: ${lives}`, canvas.width - 10, 40);

        // Check for ball-paddle collision
        if (objects.ball.checkPaddleCollision(objects.paddle)) {
          objects.ball.bounceOffPaddle(objects.paddle);
        }

        // Check for ball-brick collisions
        objects.bricks.forEach((brick, index) => {
          if (!brick.isDestroyed() && objects.ball.checkBrickCollision(brick)) {
            objects.ball.bounceOffBrick();
            if (brick.hit()) {
              setScore(prev => prev + 100);
            }
          }
        });

        // Check if ball is lost
        if (objects.ball.y + objects.ball.radius > canvas.height) {
          setLives(prev => prev - 1);
          if (lives > 1) {
            objects.ball.reset();
          } else {
            setIsGameOver(true);
          }
        }

        // Check if level is complete
        if (objects.bricks.every(brick => brick.isDestroyed())) {
          if (currentLevel < levelManagerRef.current.getMaxLevel()) {
            setCurrentLevel(prev => prev + 1);
            objects.ball.reset();
            objects.paddle.reset();
            objects.bricks = levelManagerRef.current.loadLevel(currentLevel + 1).bricks;
          } else {
            setIsVictory(true);
            setIsGameOver(true);
          }
        }
      }

      // Request next frame
      requestIdRef.current = requestAnimationFrame(gameLoop);
    };

    // Start game loop
    requestIdRef.current = requestAnimationFrame(gameLoop);

    // Cleanup
    return () => {
      if (requestIdRef.current) {
        cancelAnimationFrame(requestIdRef.current);
      }
      if (currentCanvas) {
        currentCanvas.removeEventListener('keydown', handleKeyDown);
        currentCanvas.removeEventListener('keyup', handleKeyUp);
      }
    };
  }, [isPaused, isGameOver, lives, currentLevel]); // Only re-run effect when game state changes

  const handleRestart = () => {
    if (gameObjectsRef.current) {
      gameObjectsRef.current.ball.reset();
      gameObjectsRef.current.paddle.reset();
      gameObjectsRef.current.bricks = levelManagerRef.current.loadLevel(1).bricks;
      setScore(0);
      setLives(3);
      setCurrentLevel(1);
      setIsGameOver(false);
      setIsVictory(false);
      setIsPaused(false);
    }
  };

  const navigate = useNavigate();
  const handleMainMenu = () => {
    if (requestIdRef.current) {
      cancelAnimationFrame(requestIdRef.current);
    }
    navigate('/');
  };

  return (
    <div className="game-container">
      <canvas
        ref={canvasRef}
        className="game-canvas"
      />

      {isPaused && !isGameOver && (
        <PauseMenu
          onResume={() => setIsPaused(false)}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
        />
      )}

      {isGameOver && (
        <GameOver
          score={score}
          level={currentLevel}
          isVictory={isVictory}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
        />
      )}
    </div>
  );
};

export default GameCanvas;
