class Ball {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.radius = 8;
    this.speed = 5;
    this.dx = 0;  // Start with no movement
    this.dy = 0;  // Start with no movement
    this.initialX = x;
    this.initialY = y;
    this.isLaunched = false;
  }

  reset() {
    this.x = this.initialX;
    this.y = this.initialY;
    this.dx = 0;
    this.dy = 0;
    this.isLaunched = false;
  }

  launch() {
    if (!this.isLaunched) {
      this.dx = this.speed;
      this.dy = -this.speed;
      this.isLaunched = true;
    }
  }

  stickToPaddle(paddle) {
    if (!this.isLaunched) {
      this.x = paddle.x + paddle.width / 2;
      this.y = paddle.y - this.radius;
    }
  }

  update() {
    if (!this.isLaunched) return;

    // Move ball
    this.x += this.dx;
    this.y += this.dy;

    // Check wall collisions
    if (this.x - this.radius <= 0 || this.x + this.radius >= 800) {
      this.dx = -this.dx;
    }
    if (this.y - this.radius <= 0) {
      this.dy = -this.dy;
    }
  }

  draw(ctx) {
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
    ctx.fillStyle = '#FFFFFF';
    ctx.fill();
    ctx.closePath();
  }

  checkPaddleCollision(paddle) {
    const distX = Math.abs(this.x - paddle.x - paddle.width/2);
    const distY = Math.abs(this.y - paddle.y - paddle.height/2);

    if (distX > (paddle.width/2 + this.radius)) { return false; }
    if (distY > (paddle.height/2 + this.radius)) { return false; }

    if (distX <= (paddle.width/2)) { return true; }
    if (distY <= (paddle.height/2)) { return true; }

    const dx = distX - paddle.width/2;
    const dy = distY - paddle.height/2;
    return (dx * dx + dy * dy <= (this.radius * this.radius));
  }

  checkBrickCollision(brick) {
    const distX = Math.abs(this.x - brick.x - brick.width/2);
    const distY = Math.abs(this.y - brick.y - brick.height/2);

    if (distX > (brick.width/2 + this.radius)) { return false; }
    if (distY > (brick.height/2 + this.radius)) { return false; }

    if (distX <= (brick.width/2)) { return true; }
    if (distY <= (brick.height/2)) { return true; }

    const dx = distX - brick.width/2;
    const dy = distY - brick.height/2;
    return (dx * dx + dy * dy <= (this.radius * this.radius));
  }

  bounceOffPaddle(paddle) {
    // Calculate relative position of ball on paddle
    const relativeIntersectX = (this.x - (paddle.x + paddle.width/2));
    // Normalize the value
    const normalizedRelativeIntersectX = relativeIntersectX / (paddle.width/2);
    // Calculate bounce angle (maximum 75 degrees)
    const bounceAngle = normalizedRelativeIntersectX * Math.PI/3;

    // Update velocity based on bounce angle
    this.dx = this.speed * Math.sin(bounceAngle);
    this.dy = -this.speed * Math.cos(bounceAngle);
  }

  bounceOffBrick() {
    // Simple brick collision - just reverse vertical direction
    this.dy = -this.dy;
  }
}

export default Ball;
