      const angle = -Math.PI / 4; // -45 degrees
      this.dx = this.speed * Math.cos(angle);
      this.dy = this.speed * Math.sin(angle);
    }
  }

  reset() {
    this.x = this.initialX;
    this.y = this.initialY;
    this.dx = 0;
    this.dy = 0;
    this.isLaunched = false;
    this.speed = this.baseSpeed;
  }

  stickToPaddle(paddle) {
    if (!this.isLaunched) {
      this.x = paddle.x + paddle.width / 2;
      this.y = paddle.y - this.radius;
    }
  }

  bounceOffPaddle(paddle) {
    if (!this.isLaunched) return;

    // Calculate relative position of ball hit on paddle
    const relativeIntersectX = (this.x - (paddle.x + paddle.width / 2)) / (paddle.width / 2);

    // Calculate bounce angle (-60 to 60 degrees)
    const bounceAngle = relativeIntersectX * Math.PI / 3;

    // Set new velocity while maintaining speed
    this.dx = this.speed * Math.sin(bounceAngle);
    this.dy = -Math.abs(this.speed * Math.cos(bounceAngle)); // Ensure upward motion

    // Ensure ball is above paddle
    this.y = paddle.y - this.radius;
  }

  bounceOffBrick(brick) {
    if (!this.isLaunched) return;

    // Determine collision side
    const ballCenterX = this.x;
    const ballCenterY = this.y;
    const brickCenterX = brick.x + brick.width / 2;
    const brickCenterY = brick.y + brick.height / 2;

    const dx = Math.abs(ballCenterX - brickCenterX);
    const dy = Math.abs(ballCenterY - brickCenterY);

    // Determine collision side and bounce
    if (dx > dy) {
      // Horizontal collision
      this.dx = -this.dx;
      // Adjust x position to prevent sticking
      if (ballCenterX < brickCenterX) {
        this.x = brick.x - this.radius;
