class SoundManager {
  constructor() {
    this.sounds = {};
    this.loadSounds();
  }

  loadSounds() {
    this.sounds.brickHit = new Audio('/assets/sounds/brick_hit.wav');
    this.sounds.paddleHit = new Audio('/assets/sounds/paddle_hit.wav');

    // Preload sounds
    Object.values(this.sounds).forEach(sound => {
      sound.load();
    });
  }

  play(soundName) {
    const sound = this.sounds[soundName];
    if (sound) {
      // Clone and play the sound to allow overlapping
      const soundClone = sound.cloneNode();
      soundClone.volume = 0.5; // Set volume to 50%
      soundClone.play().catch(error => {
        console.warn('Error playing sound:', error);
      });
    }
  }
}

const soundManager = new SoundManager();
export default soundManager;
