class SoundManager {
  constructor() {
    this.sounds = {};
    this.loadSounds();
  }

  loadSounds() {
    // Create Audio objects
    this.brickHitSound = new Audio();
    this.paddleHitSound = new Audio();
    this.ballLaunchSound = new Audio();

    // Set sources
    this.brickHitSound.src = `${process.env.PUBLIC_URL}/sounds/brick_hit.wav`;
    this.paddleHitSound.src = `${process.env.PUBLIC_URL}/sounds/paddle_hit.wav`;
    this.ballLaunchSound.src = `${process.env.PUBLIC_URL}/sounds/paddle_hit.wav`; // Reusing paddle hit sound for now

    // Preload sounds
    this.brickHitSound.load();
    this.paddleHitSound.load();
    this.ballLaunchSound.load();
  }

  playBrickHit() {
    const sound = this.brickHitSound.cloneNode();
    sound.volume = 0.5;
    sound.play().catch(error => {
      console.warn('Error playing brick hit sound:', error);
    });
  }

  playPaddleHit() {
    const sound = this.paddleHitSound.cloneNode();
    sound.volume = 0.5;
    sound.play().catch(error => {
      console.warn('Error playing paddle hit sound:', error);
    });
  }

  playBallLaunch() {
    const sound = this.ballLaunchSound.cloneNode();
    sound.volume = 0.3; // Slightly lower volume for launch
    sound.play().catch(error => {
      console.warn('Error playing ball launch sound:', error);
    });
  }

  cleanup() {
    // Clean up audio resources
    if (this.brickHitSound) {
      this.brickHitSound.src = '';
      this.brickHitSound = null;
    }
    if (this.paddleHitSound) {
      this.paddleHitSound.src = '';
      this.paddleHitSound = null;
    }
    if (this.ballLaunchSound) {
      this.ballLaunchSound.src = '';
      this.ballLaunchSound = null;
    }
  }
}

export default SoundManager;
