import React, { useRef, useEffect, useState } from 'react';
import { useGameLoop } from './GameLoop';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import GameOver from '../UI/GameOver';
import PauseMenu from '../UI/PauseMenu';
import '../../styles/components/GameCanvas.css';
import '../../styles/GameOver.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const [gameObjects, setGameObjects] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const { 
    start, 
    stop, 
    score, 
    currentLevel, 
    lives,
    isGameOver,
    isVictory,
    resetGame,
    isRunning 
  } = useGameLoop();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = 800;
    canvas.height = 600;

    const ctx = canvas.getContext('2d');

    // Initialize game objects
    const objects = {
      ball: new Ball(canvas.width / 2, canvas.height - 30),
      paddle: new Paddle(canvas.width / 2, canvas.height - 20),
      bricks: [] // Will be populated by LevelManager
    };
    setGameObjects(objects);

    // Start game loop
    start(ctx, objects);

    // Add pause event listener
    const handlePause = (e) => {
      if (e.key === 'Escape') {
        setIsPaused(prev => !prev);
        if (!isRunning) {
          start(ctx, objects);
        } else {
          stop();
        }
      }
    };

    document.addEventListener('keydown', handlePause);

    return () => {
      stop();
      if (objects.paddle) {
        objects.paddle.cleanup();
      }
      document.removeEventListener('keydown', handlePause);
    };
  }, []);

  const handleRestart = () => {
    if (gameObjects) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      resetGame(gameObjects);
      setIsPaused(false);
      start(ctx, gameObjects);
    }
  };

  const handleResume = () => {
    if (gameObjects) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      setIsPaused(false);
      start(ctx, gameObjects);
    }
  };

  return (
    <div className="game-container">
      <canvas
        ref={canvasRef}
        className="game-canvas"
      />

      {isPaused && !isGameOver && (
        <PauseMenu
          onResume={handleResume}
          onRestart={handleRestart}
        />
      )}

      {isGameOver && (
        <GameOver
          score={score}
          level={currentLevel}
          onRestart={handleRestart}
          isVictory={isVictory}
        />
      )}
    </div>
  );
};

export default GameCanvas;
