import React, { useEffect, useRef, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import Brick from './entities/Brick'; // Import Brick
import { useGameLoop } from './GameLoop';
import LevelManager from './LevelManager';
import PauseMenu from '../UI/PauseMenu';
import GameOver from '../UI/GameOver';
import SoundManager from '../../utils/SoundManager';
import '../../styles/components/GameCanvas.css';

const GameCanvas = () => {
  const location = useLocation();
  const canvasRef = useRef(null);
  const gameObjectsRef = useRef(null);
  const requestIdRef = useRef({ id: null, lastTime: 0 });
  const levelManagerRef = useRef(new LevelManager());
  const soundManagerRef = useRef(null);

  // Get the selected level from navigation state, default to 1
  const initialLevel = location.state?.selectedLevel || 1;

  const {
    isPaused,
    isGameOver,
    setScore,
    score,
    lives,
    setLives,
    currentLevel,
    setCurrentLevel,
    setIsPaused,
    setIsGameOver,
    setIsVictory,
    isVictory,
    resetLevel
  } = useGameLoop(initialLevel);

  // Initialize sound manager
  useEffect(() => {
    soundManagerRef.current = new SoundManager();
    return () => {
      if (soundManagerRef.current) {
        soundManagerRef.current.cleanup();
      }
    };
  }, []);

  const drawHUD = useCallback((ctx) => {
    if (!ctx || !canvasRef.current) return;

    ctx.save();
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '20px Arial';

    // Draw score
    ctx.textAlign = 'left';
    ctx.fillText(`Score: ${score}`, 10, 30);

    // Draw level
    ctx.textAlign = 'center';
    ctx.fillText(`Level ${currentLevel}`, canvasRef.current.width / 2, 30);

    // Draw lives
    ctx.textAlign = 'right';
    ctx.fillText(`Lives: ${lives}`, canvasRef.current.width - 10, 30);
    ctx.restore();
  }, [score, lives, currentLevel]);

  const resetBallAndPaddle = useCallback(() => {
    if (!gameObjectsRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const objects = gameObjectsRef.current;

    // Reset paddle position
    objects.paddle.x = canvas.width / 2 - objects.paddle.width / 2;
    objects.paddle.y = canvas.height - 40;
    objects.paddle.leftPressed = false;
    objects.paddle.rightPressed = false;

    // Reset ball position and state
    objects.ball.x = canvas.width / 2;
    objects.ball.y = canvas.height - 60;
    objects.ball.dx = 0;
    objects.ball.dy = 0;
    objects.ball.isLaunched = false;
  }, []);

  const resetLevelState = useCallback(() => {
    if (!gameObjectsRef.current) return;

    // Get fresh level data for the current level
    const levelData = levelManagerRef.current.loadLevel(currentLevel);

    // Create new Brick instances to ensure clean state
    gameObjectsRef.current.bricks = levelData.bricks.map(brick => 
      new Brick(brick.x, brick.y, brick.width, brick.height, brick.type)
    );

    resetBallAndPaddle();
  }, [currentLevel, resetBallAndPaddle]);

  const handleRestart = useCallback(() => {
    setIsPaused(false);
    setIsGameOver(false);
    setIsVictory(false);
    setScore(0);
    setLives(3);
    setCurrentLevel(1);

    // Reset level manager to ensure fresh state
    levelManagerRef.current = new LevelManager();
    resetLevelState();

    if (gameObjectsRef.current) {
      gameObjectsRef.current.ball.isLaunched = false;
    }
  }, [setIsPaused, setIsGameOver, setIsVictory, setScore, setLives, setCurrentLevel, resetLevelState]);

  const handleBallOut = useCallback(() => {
    const newLives = lives - 1;
    setLives(newLives);

    if (newLives <= 0) {
      setIsGameOver(true);
      setIsVictory(false);
      handleRestart();
    } else {
      resetBallAndPaddle();
    }
  }, [lives, setLives, setIsGameOver, setIsVictory, handleRestart, resetBallAndPaddle]);

  const handleKeyDown = useCallback((e) => {
    const objects = gameObjectsRef.current;
    if (!objects) return;

    const key = e.key.toLowerCase();
    switch (key) {
      case 'arrowleft':
        if (!isPaused) objects.paddle.moveLeft();
        break;
      case 'arrowright':
        if (!isPaused) objects.paddle.moveRight();
        break;
      case ' ':
        if (isGameOver) {
          handleRestart();
        } else if (!isPaused && objects.ball && !objects.ball.isLaunched) {
          objects.ball.launch();
        }
        break;
      case 'escape':
        setIsPaused(!isPaused);
        break;
      default:
        break;
    }
  }, [isPaused, isGameOver, handleRestart]);

  const handleKeyUp = useCallback((e) => {
    const objects = gameObjectsRef.current;
    if (!objects) return;

    const key = e.key.toLowerCase();
    switch (key) {
      case 'arrowleft':
        objects.paddle.stopLeft();
        break;
      case 'arrowright':
        objects.paddle.stopRight();
        break;
      default:
        break;
    }
  }, []);

  const handleResume = useCallback(() => {
    setIsPaused(false);
  }, [setIsPaused]);

  const drawGameObjects = useCallback((ctx) => {
    if (!ctx || !gameObjectsRef.current) return;

    const objects = gameObjectsRef.current;
    ctx.save();

    // Draw bricks
    objects.bricks.forEach(brick => {
      if (!brick.isDestroyed()) {
        brick.draw(ctx);
      }
    });

    // Draw paddle and ball
    objects.paddle.draw(ctx);
    objects.ball.draw(ctx);

    ctx.restore();
  }, []);

  const animate = useCallback((timestamp) => {
    if (isPaused || isGameOver) return;

    const deltaTime = timestamp - (requestIdRef.current.lastTime || timestamp);
    requestIdRef.current.lastTime = timestamp;

    const ctx = canvasRef.current?.getContext('2d', { alpha: false });
    const objects = gameObjectsRef.current;

    if (!ctx || !objects) return;

    // Clear canvas
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Update game objects
    if (objects.ball.y + objects.ball.radius > canvasRef.current.height) {
      handleBallOut();
    } else {
      objects.paddle.update(deltaTime / 1000);
      objects.ball.update(deltaTime / 1000);

      // Handle paddle collision
      if (objects.ball.checkPaddleCollision(objects.paddle)) {
        objects.ball.bounceOffPaddle(objects.paddle);
        if (soundManagerRef.current) {
          soundManagerRef.current.playPaddleHit();
        }
      }

      // Only stick to paddle if not launched
      if (!objects.ball.isLaunched) {
        objects.ball.stickToPaddle(objects.paddle);
      }

      // Check brick collisions
      let remainingBricks = 0;
      let brickHit = false;

      for (const brick of objects.bricks) {
        if (!brick.isDestroyed()) {
          remainingBricks++;
          if (!brickHit && objects.ball.checkBrickCollision(brick)) {
            objects.ball.bounceOffBrick(brick);
            brickHit = true;
            if (brick.hit()) {
              setScore(prev => prev + 100);
              if (soundManagerRef.current) {
                soundManagerRef.current.playBrickHit();
              }
            }
          }
        }
      }

      // Check if level is complete
      if (objects.bricks.length > 0 && remainingBricks === 0) {
        const nextLevel = currentLevel + 1;
        if (nextLevel <= levelManagerRef.current.getMaxLevel()) {
          setCurrentLevel(nextLevel);
        } else {
          setIsGameOver(true);
          setIsVictory(true);
        }
      }
    }

    // Draw game objects
    drawGameObjects(ctx);
    drawHUD(ctx);

    // Request next frame
    requestIdRef.current.id = requestAnimationFrame(animate);
  }, [drawGameObjects, drawHUD, handleBallOut, currentLevel, isPaused, isGameOver, 
      setCurrentLevel, setIsGameOver, setIsVictory, setScore]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    canvas.width = 800;
    canvas.height = 600;

    // Initialize game objects
    gameObjectsRef.current = {
      paddle: new Paddle(canvas.width / 2 - 50, canvas.height - 40),
      ball: new Ball(canvas.width / 2, canvas.height - 60),
      bricks: []
    };

    // Load initial level
    const levelData = levelManagerRef.current.loadLevel(currentLevel);
    gameObjectsRef.current.bricks = levelData.bricks.map(brick => 
      new Brick(brick.x, brick.y, brick.width, brick.height, brick.type)
    );

    // Start animation
    requestIdRef.current.id = requestAnimationFrame(animate);

    // Add event listeners
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      if (requestIdRef.current.id) {
        cancelAnimationFrame(requestIdRef.current.id);
      }
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [currentLevel, handleKeyDown, handleKeyUp, animate]);

  useEffect(() => {
    if (!gameObjectsRef.current) return;

    // Get fresh level data for the current level
    const levelData = levelManagerRef.current.loadLevel(currentLevel);

    // Create new Brick instances to ensure clean state
    gameObjectsRef.current.bricks = levelData.bricks.map(brick => 
      new Brick(brick.x, brick.y, brick.width, brick.height, brick.type)
    );

    resetBallAndPaddle();
  }, [currentLevel, resetBallAndPaddle]);

  return (
    <div className="game-container">
      <div className="canvas-wrapper">
        <canvas
          ref={canvasRef}
          className="game-canvas"
          width={800}
          height={600}
          tabIndex={0}
        />
        {isPaused && (
          <div className="pause-overlay">
            <PauseMenu
              onResume={handleResume}
              onRestart={handleRestart}
            />
          </div>
        )}
        {isGameOver && (
          <div className="game-over-overlay">
            <GameOver
              score={score}
              level={currentLevel}
              onRestart={handleRestart}
              isVictory={isVictory}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default GameCanvas;
