import React, { useRef, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import PauseMenu from '../UI/PauseMenu';
import GameOver from '../UI/GameOver';
import LevelManager from './LevelManager';
import '../../styles/components/GameCanvas.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const [gameObjects, setGameObjects] = useState(null);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [currentLevel, setCurrentLevel] = useState(1);
  const [isPaused, setIsPaused] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isVictory, setIsVictory] = useState(false);
  const [animationFrameId, setAnimationFrameId] = useState(null);

  // Create levelManager ref to avoid dependency issues
  const levelManagerRef = useRef(new LevelManager());

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = 800;
    canvas.height = 600;
    const ctx = canvas.getContext('2d');

    // Initialize game objects
    const objects = {
      ball: new Ball(canvas.width / 2, canvas.height - 30),
      paddle: new Paddle(canvas.width / 2 - 50, canvas.height - 20),
      bricks: levelManagerRef.current.loadLevel(currentLevel).bricks
    };
    setGameObjects(objects);

    let lastTime = 0;
    const targetFPS = 60;

    // Handle spacebar and mouse click for ball launch
    const handleLaunch = (e) => {
      if (!isPaused && !isGameOver && objects.ball && !objects.ball.isLaunched) {
        if ((e.type === 'keydown' && e.code === 'Space') || e.type === 'click') {
          e.preventDefault();
          objects.ball.launch();
        }
      }
    };

    window.addEventListener('keydown', handleLaunch);
    canvas.addEventListener('click', handleLaunch);

    // Game loop function
    const gameLoop = (timestamp) => {
      if (!lastTime) {
        lastTime = timestamp;
      }

      const currentTime = timestamp;
      lastTime = currentTime;

      if (!isPaused && !isGameOver) {
        // Update game objects
        objects.paddle.update();

        // Make ball stick to paddle if not launched
        if (!objects.ball.isLaunched) {
          objects.ball.stickToPaddle(objects.paddle);
        } else {
          objects.ball.update();
        }

        // Check for ball-paddle collision
        if (objects.ball.checkPaddleCollision(objects.paddle)) {
          objects.ball.bounceOffPaddle(objects.paddle);
        }

        // Check for ball-brick collisions
        objects.bricks.forEach((brick, index) => {
          if (!brick.isDestroyed() && objects.ball.checkBrickCollision(brick)) {
            objects.ball.bounceOffBrick();
            if (brick.hit()) {
              setScore(prev => prev + 100);
              if (brick.isExplosive()) {
                objects.bricks.forEach((nearbyBrick, i) => {
                  if (i !== index && !nearbyBrick.isDestroyed()) {
                    const dx = nearbyBrick.x - brick.x;
                    const dy = nearbyBrick.y - brick.y;
                    const distance = Math.sqrt(dx * dx + dy * dy);
                    if (distance < 100) {
                      nearbyBrick.hit();
                      setScore(prev => prev + 50);
                    }
                  }
                });
              }
            }
          }
        });

        // Check if ball is lost
        if (objects.ball.y + objects.ball.radius > canvas.height) {
          setLives(prev => prev - 1);
          if (lives > 1) {
            objects.ball.reset();
          } else {
            setIsGameOver(true);
          }
        }

        // Check if level is complete
        if (objects.bricks.every(brick => brick.isDestroyed() || brick.type === 'unbreakable')) {
          if (currentLevel < levelManagerRef.current.getMaxLevel()) {
            setCurrentLevel(prev => prev + 1);
            objects.ball.reset();
            objects.paddle.reset();
            objects.bricks = levelManagerRef.current.loadLevel(currentLevel + 1).bricks;
          } else {
            setIsVictory(true);
            setIsGameOver(true);
          }
        }

        // Clear canvas
        ctx.fillStyle = '#000000';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        // Draw objects
        objects.bricks.forEach(brick => brick.draw(ctx));
        objects.ball.draw(ctx);
        objects.paddle.draw(ctx);

        // Draw HUD
        ctx.font = '16px Courier New';
        ctx.fillStyle = '#00FF00';

        // Score on the left
        ctx.textAlign = 'left';
        ctx.fillText(`Score: ${score}`, 10, 40);

        // Level in the center
        ctx.textAlign = 'center';
        ctx.fillText(`Level: ${currentLevel}`, canvas.width / 2, 40);

        // Lives on the right
        ctx.textAlign = 'right';
        ctx.fillText(`Lives: ${lives}`, canvas.width - 10, 40);
      }

      // Request next frame
      const id = requestAnimationFrame(gameLoop);
      setAnimationFrameId(id);
    };

    // Start game loop
    gameLoop();

    // Add pause event listener
    const handlePause = (e) => {
      if (e.key === 'Escape') {
        setIsPaused(prev => !prev);
      }
    };

    window.addEventListener('keydown', handlePause);

    // Cleanup
    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
      if (objects.paddle) {
        objects.paddle.cleanup();
      }
      window.removeEventListener('keydown', handlePause);
      window.removeEventListener('keydown', handleLaunch);
      canvas.removeEventListener('click', handleLaunch);
    };
  }, [currentLevel, score, lives, isPaused, isGameOver, animationFrameId]);

  const handleRestart = () => {
    if (gameObjects) {
      setScore(0);
      setLives(3);
      setCurrentLevel(1);
      setIsGameOver(false);
      setIsVictory(false);
      setIsPaused(false);

      gameObjects.ball.reset();
      gameObjects.paddle.reset();
      gameObjects.bricks = levelManagerRef.current.loadLevel(1).bricks;
    }
  };

  const handleResume = () => {
    setIsPaused(false);
  };

  const navigate = useNavigate();
  const handleMainMenu = () => {
    if (animationFrameId) {
      cancelAnimationFrame(animationFrameId);
    }
    navigate('/');
  };

  return (
    <div className="game-container">
      <canvas
        ref={canvasRef}
        className="game-canvas"
      />

      {isPaused && !isGameOver && (
        <PauseMenu
          onResume={handleResume}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
        />
      )}

      {isGameOver && (
        <GameOver
          score={score}
          level={currentLevel}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
          isVictory={isVictory}
        />
      )}
    </div>
  );
};

export default GameCanvas;
