class Paddle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.width = 100;
    this.height = 20;
    this.speed = 1000; // Increased speed
    this.initialX = x;
    this.initialY = y;
    this.leftPressed = false;
    this.rightPressed = false;
    this.canvasWidth = 800;
  }

  reset() {
    this.x = this.canvasWidth / 2 - this.width / 2;
    this.leftPressed = false;
    this.rightPressed = false;
  }

  moveLeft() {
    this.leftPressed = true;
  }

  moveRight() {
    this.rightPressed = true;
  }

  stopLeft() {
    this.leftPressed = false;
  }

  stopRight() {
    this.rightPressed = false;
  }

  update(deltaTime) {
    if (this.leftPressed) {
      const newX = this.x - this.speed * deltaTime;
      this.x = Math.max(0, newX);
    }
    if (this.rightPressed) {
      const newX = this.x + this.speed * deltaTime;
      this.x = Math.min(this.canvasWidth - this.width, newX);
    }
  }

  draw(ctx) {
    ctx.fillStyle = '#00FF00';
    ctx.fillRect(Math.round(this.x), this.y, this.width, this.height);
  }
}

export default Paddle;
