import React, { useEffect, useRef, useCallback } from 'react';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import { useGameLoop } from './GameLoop';
import LevelManager from './LevelManager';
import './GameCanvas.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const gameObjectsRef = useRef(null);
  const requestIdRef = useRef({ id: null, lastTime: null });
  const levelManagerRef = useRef(new LevelManager());

  const {
    isRunning,
    isPaused,
    isGameOver,
    setScore,
    score,
    lives,
    setLives,
    currentLevel,
    setIsPaused,
    setIsGameOver,
    setIsVictory,
    stop
  } = useGameLoop();

  // Draw HUD function
  const drawHUD = useCallback((ctx) => {
    ctx.save();
    ctx.font = '16px Arial';
    ctx.fillStyle = '#00FF00';
    ctx.textAlign = 'left';
    ctx.fillText(`Score: ${score}`, 10, 30);
    ctx.textAlign = 'center';
    ctx.fillText(`Level: ${currentLevel}`, canvasRef.current.width / 2, 30);
    ctx.textAlign = 'right';
    ctx.fillText(`Lives: ${lives}`, canvasRef.current.width - 10, 30);
    ctx.restore();
  }, [score, lives, currentLevel]);

  // Handle ball out of bounds
  const handleBallOut = useCallback((objects) => {
    setLives(l => l - 1);
    if (lives > 1) {
      objects.ball.reset();
    } else {
      setIsGameOver(true);
      stop();
    }
  }, [lives, setLives, setIsGameOver, stop]);

  const resetLevel = useCallback(() => {
    if (gameObjectsRef.current) {
      const levelData = levelManagerRef.current.loadLevel(currentLevel);
      gameObjectsRef.current.bricks = levelData.bricks;
      gameObjectsRef.current.ball.reset();
      gameObjectsRef.current.paddle.reset();
    }
  }, [currentLevel]);

  // Game loop
  const gameLoop = useCallback((timestamp) => {
    if (!canvasRef.current) return;
    const ctx = canvasRef.current.getContext('2d', { alpha: false });

    if (!requestIdRef.current.lastTime) {
      requestIdRef.current.lastTime = timestamp;
      requestIdRef.current.id = requestAnimationFrame(gameLoop);
      return;
    }

    const deltaTime = Math.min((timestamp - requestIdRef.current.lastTime) / 1000, 0.032);
    requestIdRef.current.lastTime = timestamp;

    if (!isPaused && !isGameOver) {
      const objects = gameObjectsRef.current;

      // Clear canvas
      ctx.fillStyle = 'rgb(0, 0, 0)';
      ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

      // Update game objects
      objects.paddle.update(deltaTime);

      if (!objects.ball.isLaunched) {
        objects.ball.stickToPaddle(objects.paddle);
      } else {
        objects.ball.update(deltaTime);
      }

      // Draw game objects
      ctx.save();
      objects.bricks.forEach(brick => {
        if (!brick.isDestroyed()) {
          brick.draw(ctx);
        }
      });
      objects.ball.draw(ctx);
      objects.paddle.draw(ctx);
      ctx.restore();

      // Draw HUD
      drawHUD(ctx);

      // Handle collisions
      if (objects.ball.checkPaddleCollision(objects.paddle)) {
        objects.ball.bounceOffPaddle(objects.paddle);
      }

      objects.bricks.forEach((brick) => {
        if (!brick.isDestroyed() && objects.ball.checkBrickCollision(brick)) {
          objects.ball.bounceOffBrick(brick);
          if (brick.hit()) {
            setScore(s => s + 100);
          }
        }
      });

      // Check if ball is out of bounds
      if (objects.ball.isLaunched && objects.ball.y > canvasRef.current.height) {
        handleBallOut(objects);
      }
    }

    requestIdRef.current.id = requestAnimationFrame(gameLoop);
  }, [isPaused, isGameOver, handleBallOut, drawHUD]);

  // Keyboard handlers
  const handleKeyDown = useCallback((e) => {
    if (!isPaused && !isGameOver) {
      const objects = gameObjectsRef.current;
      switch (e.key.toLowerCase()) {
        case 'a':
        case 'arrowleft':
          objects.paddle.moveLeft();
          break;
        case 'd':
        case 'arrowright':
          objects.paddle.moveRight();
          break;
        case ' ':
          if (objects.ball) objects.ball.launch();
          break;
        case 'escape':
          setIsPaused(prev => !prev);
          break;
        default:
          break;
      }
    }
  }, [isPaused, isGameOver, setIsPaused]);

  const handleKeyUp = useCallback((e) => {
    if (!isPaused && !isGameOver) {
      const objects = gameObjectsRef.current;
      switch (e.key.toLowerCase()) {
        case 'a':
        case 'arrowleft':
          objects.paddle.stopLeft();
          break;
        case 'd':
        case 'arrowright':
          objects.paddle.stopRight();
          break;
        default:
          break;
      }
    }
  }, [isPaused, isGameOver]);

  const handleRestart = useCallback(() => {
    if (gameObjectsRef.current) {
      resetLevel();
      setScore(0);
      setLives(3);
      setIsGameOver(false);
      setIsVictory(false);
      setIsPaused(false);
    }
  }, [resetLevel, setScore, setLives, setIsGameOver, setIsVictory, setIsPaused]);

  // Setup effect
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = 800;
    canvas.height = 600;

    // Initialize game objects
    const paddleY = canvas.height - 40;
    gameObjectsRef.current = {
      paddle: new Paddle(canvas.width / 2 - 50, paddleY),
      ball: new Ball(canvas.width / 2, paddleY - 10),
      bricks: []
    };

    // Load initial level
    resetLevel();

    // Start game loop
    requestIdRef.current.id = requestAnimationFrame(gameLoop);

    // Add event listeners
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      if (requestIdRef.current.id) {
        cancelAnimationFrame(requestIdRef.current.id);
      }
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [gameLoop, handleKeyDown, handleKeyUp, resetLevel]);

  return (
    <canvas
      ref={canvasRef}
      className="game-canvas"
      tabIndex={0}
    />
  );
};

export default GameCanvas;
