class Paddle {
  constructor(x, y) {
    this.width = 75;
    this.height = 10;
    this.initialX = x;
    this.initialY = y;
    this.x = x - this.width / 2;
    this.y = y;
    this.speed = 7;
    this.moveLeft = false;
    this.moveRight = false;

    // Add event listeners for keyboard controls
    document.addEventListener('keydown', this.handleKeyDown.bind(this));
    document.addEventListener('keyup', this.handleKeyUp.bind(this));
    // Add touch event listeners for mobile
    document.addEventListener('touchstart', this.handleTouchStart.bind(this));
    document.addEventListener('touchmove', this.handleTouchMove.bind(this));
    document.addEventListener('touchend', this.handleTouchEnd.bind(this));
  }

  handleKeyDown(e) {
    if (e.key === 'ArrowLeft' || e.key === 'a') this.moveLeft = true;
    if (e.key === 'ArrowRight' || e.key === 'd') this.moveRight = true;
    if (e.key === 'Escape') {
      // Emit pause event
      const pauseEvent = new CustomEvent('gamePause');
      document.dispatchEvent(pauseEvent);
    }
  }

  handleKeyUp(e) {
    if (e.key === 'ArrowLeft' || e.key === 'a') this.moveLeft = false;
    if (e.key === 'ArrowRight' || e.key === 'd') this.moveRight = false;
  }

  handleTouchStart(e) {
    const touch = e.touches[0];
    this.lastTouchX = touch.clientX;
  }

  handleTouchMove(e) {
    e.preventDefault();
    const touch = e.touches[0];
    const deltaX = touch.clientX - this.lastTouchX;
    this.x += deltaX;
    this.lastTouchX = touch.clientX;

    // Keep paddle within bounds
    if (this.x < 0) this.x = 0;
    if (this.x + this.width > 800) this.x = 800 - this.width;
  }

  handleTouchEnd() {
    this.lastTouchX = null;
  }

  update() {
    if (this.moveLeft) this.x -= this.speed;
    if (this.moveRight) this.x += this.speed;

    // Keep paddle within bounds
    if (this.x < 0) this.x = 0;
    if (this.x + this.width > 800) this.x = 800 - this.width;
  }

  reset() {
    this.x = this.initialX - this.width / 2;
    this.y = this.initialY;
    this.moveLeft = false;
    this.moveRight = false;
  }

  cleanup() {
    // Remove event listeners
    document.removeEventListener('keydown', this.handleKeyDown.bind(this));
    document.removeEventListener('keyup', this.handleKeyUp.bind(this));
    document.removeEventListener('touchstart', this.handleTouchStart.bind(this));
    document.removeEventListener('touchmove', this.handleTouchMove.bind(this));
    document.removeEventListener('touchend', this.handleTouchEnd.bind(this));
  }

  draw(ctx) {
    ctx.beginPath();
    ctx.rect(this.x, this.y, this.width, this.height);
    ctx.fillStyle = '#0095DD';
    ctx.fill();
    ctx.closePath();
  }
}

export default Paddle;
