import Brick from './entities/Brick';

class LevelManager {
  constructor() {
    this.levels = this.generateLevels();
    this.currentLevel = 0;
  }

  generateLevels() {
    return [
      {
        name: 'Basic Training',
        bricks: this.createBasicLevel(),
        speed: 1,
        backgroundColor: '#1a1a1a'
      },
      {
        name: 'Advanced Challenge',
        bricks: this.createAdvancedLevel(),
        speed: 1.2,
        backgroundColor: '#1a1a2a'
      },
      {
        name: 'Expert Mode',
        bricks: this.createExpertLevel(),
        speed: 1.5,
        backgroundColor: '#1a2a2a'
      },
      {
        name: 'Master Challenge',
        bricks: this.createMasterLevel(),
        speed: 1.8,
        backgroundColor: '#2a1a2a'
      }
    ];
  }

  createBasicLevel() {
    const bricks = [];
    const brickWidth = 80;
    const brickHeight = 20;
    const padding = 0; // Remove padding between bricks
    const offsetTop = 60;
    const offsetLeft = (800 - (brickWidth * 10)) / 2; // Center 10 bricks horizontally

    // Create a simple pattern for beginners
    for (let row = 0; row < 3; row++) {
      for (let col = 0; col < 10; col++) {
        const x = (col * brickWidth) + offsetLeft;
        const y = (row * brickHeight) + offsetTop;
        bricks.push(new Brick(x, y, brickWidth, brickHeight, 'standard'));
      }
    }

    return bricks;
  }

  createAdvancedLevel() {
    const bricks = [];
    const brickWidth = 80;
    const brickHeight = 20;
    const padding = 0; // Remove padding between bricks
    const offsetTop = 60;
    const offsetLeft = (800 - (brickWidth * 10)) / 2; // Center 10 bricks horizontally

    // Create a more challenging pattern with different brick types
    for (let row = 0; row < 4; row++) {
      for (let col = 0; col < 10; col++) {
        const x = (col * brickWidth) + offsetLeft;
        const y = (row * brickHeight) + offsetTop;

        let type = 'standard';
        // Add durable bricks at the top
        if (row === 0) type = 'durable';
        // Add explosive bricks in the middle
        if (row === 2 && (col === 4 || col === 5)) type = 'explosive';

        bricks.push(new Brick(x, y, brickWidth, brickHeight, type));
      }
    }

    return bricks;
  }

  createExpertLevel() {
    const bricks = [];
    const brickWidth = 80;
    const brickHeight = 20;
    const padding = 0; // Remove padding between bricks
    const offsetTop = 60;
    const offsetLeft = (800 - (brickWidth * 10)) / 2; // Center 10 bricks horizontally

    // Create a complex pattern with all brick types
    for (let row = 0; row < 5; row++) {
      for (let col = 0; col < 10; col++) {
        const x = (col * brickWidth) + offsetLeft;
        const y = (row * brickHeight) + offsetTop;

        let type = 'standard';
        // Create a wall of durable bricks at the top
        if (row === 0) type = 'durable';
        // Add explosive bricks in a pattern
        if (row === 2 && col % 3 === 1) type = 'explosive';
        // Add some unbreakable bricks for challenge
        if (row === 3 && col % 4 === 0) type = 'unbreakable';

        bricks.push(new Brick(x, y, brickWidth, brickHeight, type));
      }
    }

    return bricks;
  }

  createMasterLevel() {
    const bricks = [];
    const brickWidth = 80;
    const brickHeight = 20;
    const padding = 0; // Remove padding between bricks
    const offsetTop = 60;
    const offsetLeft = (800 - (brickWidth * 10)) / 2; // Center 10 bricks horizontally

    // Create an intense pattern with all brick types
    for (let row = 0; row < 6; row++) {
      for (let col = 0; col < 10; col++) {
        const x = (col * brickWidth) + offsetLeft;
        const y = (row * brickHeight) + offsetTop;

        let type = 'standard';
        // Create alternating patterns of brick types
        if (row === 0) type = 'unbreakable';
        if (row === 1 && col % 2 === 0) type = 'durable';
        if (row === 2 && col % 3 === 0) type = 'explosive';
        if (row === 3 && col % 2 === 1) type = 'durable';
        if (row === 4 && col % 4 === 0) type = 'unbreakable';

        bricks.push(new Brick(x, y, brickWidth, brickHeight, type));
      }
    }

    return bricks;
  }

  loadLevel(levelNumber) {
    this.currentLevel = Math.min(Math.max(0, levelNumber - 1), this.levels.length - 1);
    return this.levels[this.currentLevel];
  }

  isLevelComplete(bricks) {
    return bricks.every(brick => 
      brick.isDestroyed() || brick.type === 'unbreakable'
    );
  }

  getMaxLevel() {
    return this.levels.length;
  }

  getCurrentLevel() {
    return this.currentLevel + 1;
  }

  getLevelName() {
    return this.levels[this.currentLevel].name;
  }

  getLevelSpeed() {
    return this.levels[this.currentLevel].speed;
  }

  getLevelBackgroundColor() {
    return this.levels[this.currentLevel].backgroundColor;
  }
}

export default LevelManager;
