import { useState, useCallback, useRef } from 'react';
import LevelManager from './LevelManager';

export const useGameLoop = (initialLives = 3) => {
  const [isRunning, setIsRunning] = useState(false);
  const [currentLevel, setCurrentLevel] = useState(1);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(initialLives);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isVictory, setIsVictory] = useState(false);
  const levelManagerRef = useRef(new LevelManager());
  const animationFrameIdRef = useRef(null);
  const lastTimeRef = useRef(0);

  const loadLevel = useCallback((gameObjects) => {
    const levelData = levelManagerRef.current.loadLevel(currentLevel);
    gameObjects.bricks = levelData.bricks;
    if (gameObjects.ball) {
      gameObjects.ball.setSpeed(levelData.speed); // Use level speed directly
    }
    return levelData;
  }, [currentLevel]);

  const resetGame = useCallback((gameObjects) => {
    setCurrentLevel(1);
    setScore(0);
    setLives(initialLives);
    setIsGameOver(false);
    setIsVictory(false);
    if (gameObjects.ball) gameObjects.ball.reset();
    if (gameObjects.paddle) gameObjects.paddle.reset();
    loadLevel(gameObjects);
  }, [initialLives, loadLevel]);

  const handleBallOut = useCallback((gameObjects) => {
    setLives(prev => {
      const newLives = prev - 1;
      if (newLives <= 0) {
        setIsGameOver(true);
        setIsVictory(false);
        stop();
      }
      return newLives;
    });

    // Reset ball and paddle position if game isn't over
    if (lives > 1) {
      gameObjects.ball.reset();
      gameObjects.paddle.reset();
    }
  }, [lives]);

  const update = useCallback((ctx, gameObjects) => {
    const { ball, paddle, bricks = [] } = gameObjects;

    // Clear canvas
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    // Update game objects
    ball.update();
    paddle.update();

    // Check if ball is out
    if (ball.y + ball.radius > ctx.canvas.height) {
      handleBallOut(gameObjects);
      return;
    }

    // Check paddle collision
    if (ball.checkCollision(paddle)) {
      ball.bounceOffPaddle(paddle);
    }

    // Check brick collisions
    bricks.forEach((brick, index) => {
      if (!brick.broken && brick.checkCollision(ball)) {
        ball.bounceOffBrick(brick);
        if (brick.hit()) {
          // Handle brick destruction
          if (brick.type === 'explosive') {
            // Destroy adjacent bricks
            bricks.forEach(otherBrick => {
              if (!otherBrick.broken && 
                  Math.abs(otherBrick.x - brick.x) <= brick.width * 2 && 
                  Math.abs(otherBrick.y - brick.y) <= brick.height * 2) {
                otherBrick.broken = true;
                setScore(prev => prev + 100);
              }
            });
          }
          setScore(prev => prev + (brick.type === 'durable' ? 150 : 100));
        }
      }
    });

    // Check if level is complete
    if (levelManagerRef.current.isLevelComplete(bricks)) {
      if (currentLevel < levelManagerRef.current.getMaxLevel()) {
        setCurrentLevel(prev => prev + 1);
        loadLevel(gameObjects);
        // Reset ball and paddle position
        ball.reset();
        paddle.reset();
      } else {
        // Game complete - Victory!
        setIsGameOver(true);
        setIsVictory(true);
        stop();
      }
    }

    // Draw game objects
    bricks.forEach(brick => brick.draw(ctx));
    ball.draw(ctx);
    paddle.draw(ctx);

    // Draw HUD
    drawHUD(ctx);
  }, [currentLevel, handleBallOut, loadLevel]);

  const drawHUD = useCallback((ctx) => {
    ctx.font = '16px Arial';
    ctx.fillStyle = '#FFFFFF';
    ctx.textAlign = 'left';
    ctx.fillText(`Score: ${score}`, 10, 20);
    ctx.fillText(`Level: ${currentLevel}`, 10, 40);

    // Draw lives
    ctx.textAlign = 'right';
    ctx.fillText(`Lives: ${lives}`, ctx.canvas.width - 10, 20);
  }, [score, currentLevel, lives]);

  const gameLoop = useCallback((ctx, gameObjects) => {
    if (!isRunning) return;

    const currentTime = performance.now();
    const deltaTime = (currentTime - lastTimeRef.current) / 1000;
    lastTimeRef.current = currentTime;

    update(ctx, gameObjects, deltaTime);
    animationFrameIdRef.current = window.requestAnimationFrame(() => gameLoop(ctx, gameObjects));
  }, [isRunning, update]);

  const start = useCallback((ctx, gameObjects) => {
    setIsRunning(true);
    lastTimeRef.current = performance.now();
    loadLevel(gameObjects);
    gameLoop(ctx, gameObjects);
  }, [gameLoop, loadLevel]);

  const stop = useCallback(() => {
    setIsRunning(false);
    if (animationFrameIdRef.current) {
      window.cancelAnimationFrame(animationFrameIdRef.current);
      animationFrameIdRef.current = null;
    }
  }, []);

  return { 
    start, 
    stop, 
    score,
    currentLevel,
    lives,
    isRunning,
    isGameOver,
    isVictory,
    resetGame,
    loadLevel,
    handleBallOut
  };
};

export default useGameLoop;
