import React, { useRef, useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Ball from './entities/Ball';
import Paddle from './entities/Paddle';
import PauseMenu from '../UI/PauseMenu';
import GameOver from '../UI/GameOver';
import LevelManager from './LevelManager';
import '../../styles/components/GameCanvas.css';

const GameCanvas = () => {
  const canvasRef = useRef(null);
  const gameObjectsRef = useRef(null);
  const requestIdRef = useRef(null);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);
  const [currentLevel, setCurrentLevel] = useState(1);
  const [isPaused, setIsPaused] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isVictory, setIsVictory] = useState(false);

  // Create levelManager ref to avoid dependency issues
  const levelManagerRef = useRef(new LevelManager());

  // Memoize the game loop callback to avoid recreating it on every render
  const gameLoop = useCallback((timestamp) => {
    if (!canvasRef.current) return;
    const ctx = canvasRef.current.getContext('2d');

    if (!requestIdRef.current.lastTime) {
      requestIdRef.current.lastTime = timestamp;
      requestIdRef.current.id = requestAnimationFrame(gameLoop);
      return;
    }

    // Cap deltaTime to prevent large jumps
    const deltaTime = Math.min((timestamp - requestIdRef.current.lastTime) / 1000, 0.016);
    requestIdRef.current.lastTime = timestamp;

    if (!isPaused && !isGameOver) {
      const objects = gameObjectsRef.current;

      // Clear canvas with alpha for smooth motion
      ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
      ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

      // Update game objects with deltaTime
      objects.paddle.update(deltaTime);

      // Make ball stick to paddle if not launched
      if (!objects.ball.isLaunched) {
        objects.ball.stickToPaddle(objects.paddle);
      } else {
        objects.ball.update(deltaTime);
      }

      // Draw game objects in correct order
      objects.bricks.forEach(brick => {
        if (!brick.isDestroyed()) {
          brick.draw(ctx);
        }
      });

      objects.ball.draw(ctx);
      objects.paddle.draw(ctx);

      // Draw debug info
      ctx.font = '12px Courier New';
      ctx.fillStyle = '#00FF00';
      ctx.textAlign = 'left';
      ctx.fillText(`Ball: x=${Math.round(objects.ball.x)}, y=${Math.round(objects.ball.y)}, launched=${objects.ball.isLaunched}`, 10, canvasRef.current.height - 60);
      ctx.fillText(`Paddle: x=${Math.round(objects.paddle.x)}, y=${Math.round(objects.paddle.y)}, left=${objects.paddle.leftPressed}, right=${objects.paddle.rightPressed}`, 10, canvasRef.current.height - 40);

      // Draw HUD
      ctx.font = '16px Courier New';
      ctx.fillStyle = '#00FF00';
      ctx.textAlign = 'left';
      ctx.fillText(`Score: ${score}`, 10, 40);
      ctx.textAlign = 'center';
      ctx.fillText(`Level: ${currentLevel}`, canvasRef.current.width / 2, 40);
      ctx.textAlign = 'right';
      ctx.fillText(`Lives: ${lives}`, canvasRef.current.width - 10, 40);

      // Check for collisions
      if (objects.ball.checkPaddleCollision(objects.paddle)) {
        objects.ball.bounceOffPaddle(objects.paddle);
      }

      objects.bricks.forEach((brick) => {
        if (!brick.isDestroyed() && objects.ball.checkBrickCollision(brick)) {
          objects.ball.bounceOffBrick();
          if (brick.hit()) {
            setScore(s => s + 100);
          }
        }
      });

      // Check if ball is lost
      if (objects.ball.y + objects.ball.radius > canvasRef.current.height) {
        setLives(l => l - 1);
        if (lives > 1) {
          objects.ball.reset();
        } else {
          setIsGameOver(true);
        }
      }

      // Check if level is complete
      if (objects.bricks.every(brick => brick.isDestroyed())) {
        if (currentLevel < levelManagerRef.current.getMaxLevel()) {
          setCurrentLevel(l => l + 1);
          objects.ball.reset();
          objects.paddle.reset();
          objects.bricks = levelManagerRef.current.loadLevel(currentLevel + 1).bricks;
        } else {
          setIsVictory(true);
          setIsGameOver(true);
        }
      }
    }

    requestIdRef.current.id = requestAnimationFrame(gameLoop);
  }, [isPaused, isGameOver, lives, currentLevel, score]); // Include all dependencies

  // Memoize keyboard handlers to avoid recreating them on every render
  const handleKeyDown = useCallback((e) => {
    if (!isPaused && !isGameOver) {
      switch (e.key.toLowerCase()) {
        case 'a':
        case 'arrowleft':
          if (gameObjectsRef.current?.paddle) {
            gameObjectsRef.current.paddle.moveLeft();
          }
          break;
        case 'd':
        case 'arrowright':
          if (gameObjectsRef.current?.paddle) {
            gameObjectsRef.current.paddle.moveRight();
          }
          break;
        case ' ':
          if (gameObjectsRef.current?.ball && !gameObjectsRef.current.ball.isLaunched) {
            gameObjectsRef.current.ball.launch();
          }
          break;
        case 'escape':
          setIsPaused(prev => !prev);
          break;
        default:
          break;
      }
    }
  }, [isPaused, isGameOver]);

  const handleKeyUp = useCallback((e) => {
    if (!isPaused && !isGameOver) {
      switch (e.key.toLowerCase()) {
        case 'a':
        case 'arrowleft':
          if (gameObjectsRef.current?.paddle) {
            gameObjectsRef.current.paddle.stopLeft();
          }
          break;
        case 'd':
        case 'arrowright':
          if (gameObjectsRef.current?.paddle) {
            gameObjectsRef.current.paddle.stopRight();
          }
          break;
        default:
          break;
      }
    }
  }, [isPaused, isGameOver]);

  // Initialize game objects and setup effect
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Set canvas size
    canvas.width = 800;
    canvas.height = 600;

    // Initialize game objects if not already initialized
    if (!gameObjectsRef.current) {
      gameObjectsRef.current = {
        ball: new Ball(canvas.width / 2, canvas.height - 30),
        paddle: new Paddle(canvas.width / 2 - 50, canvas.height - 20),
        bricks: levelManagerRef.current.loadLevel(currentLevel).bricks
      };
    }

    // Initialize requestIdRef with timing info
    requestIdRef.current = {
      id: null,
      lastTime: 0
    };

    canvas.tabIndex = 0;
    canvas.focus();
    canvas.addEventListener('keydown', handleKeyDown);
    canvas.addEventListener('keyup', handleKeyUp);

    // Start game loop
    requestIdRef.current.id = requestAnimationFrame(gameLoop);

    return () => {
      if (requestIdRef.current.id) {
        cancelAnimationFrame(requestIdRef.current.id);
      }
      canvas.removeEventListener('keydown', handleKeyDown);
      canvas.removeEventListener('keyup', handleKeyUp);
    };
  }, [gameLoop, handleKeyDown, handleKeyUp, currentLevel, isPaused, isGameOver]);

  const handleRestart = () => {
    if (gameObjectsRef.current) {
      gameObjectsRef.current.ball.reset();
      gameObjectsRef.current.paddle.reset();
      gameObjectsRef.current.bricks = levelManagerRef.current.loadLevel(1).bricks;
      setScore(0);
      setLives(3);
      setCurrentLevel(1);
      setIsGameOver(false);
      setIsVictory(false);
      setIsPaused(false);
    }
  };

  const navigate = useNavigate();
  const handleMainMenu = () => {
    if (requestIdRef.current.id) {
      cancelAnimationFrame(requestIdRef.current.id);
    }
    navigate('/');
  };

  return (
    <div className="game-container">
      <canvas
        ref={canvasRef}
        className="game-canvas"
      />
      {isPaused && !isGameOver && (
        <PauseMenu
          onResume={() => setIsPaused(false)}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
        />
      )}
      {isGameOver && (
        <GameOver
          score={score}
          level={currentLevel}
          isVictory={isVictory}
          onRestart={handleRestart}
          onMainMenu={handleMainMenu}
        />
      )}
    </div>
  );
};

export default GameCanvas;
