class Brick {
  constructor(x, y, width = 80, height = 20, type = 'standard') {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.type = type;
    this.hitPoints = this.getInitialHitPoints();
    this.destroyed = false;
    this.color = this.getColor();
  }

  getInitialHitPoints() {
    switch (this.type) {
      case 'durable':
        return 2;
      case 'unbreakable':
        return Infinity;
      case 'explosive':
        return 1;
      case 'standard':
      default:
        return 1;
    }
  }

  getColor() {
    switch (this.type) {
      case 'durable':
        return this.hitPoints === 2 ? '#FF8C00' : '#FFA500';
      case 'unbreakable':
        return '#808080';
      case 'explosive':
        return '#FF0000';
      case 'standard':
      default:
        return '#3498db';
    }
  }

  hit() {
    if (this.type === 'unbreakable') {
      return false;
    }

    this.hitPoints--;

    if (this.hitPoints <= 0) {
      this.destroyed = true;
      return true;
    }

    // Update color for durable bricks
    if (this.type === 'durable') {
      this.color = this.getColor();
    }

    return false;
  }

  isDestroyed() {
    return this.destroyed;
  }

  isExplosive() {
    return this.type === 'explosive';
  }

  draw(ctx) {
    if (this.destroyed) return;

    ctx.beginPath();
    ctx.rect(this.x, this.y, this.width, this.height);
    ctx.fillStyle = this.color;
    ctx.fill();
    ctx.strokeStyle = '#000000';
    ctx.lineWidth = 1;
    ctx.stroke();
    ctx.closePath();

    // Add visual effects based on brick type
    if (this.type === 'durable' && this.hitPoints === 2) {
      // Add a stripe pattern for durable bricks
      ctx.beginPath();
      for (let i = 0; i < this.width; i += 8) {
        ctx.moveTo(this.x + i, this.y);
        ctx.lineTo(this.x + i, this.y + this.height);
      }
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 0.5;
      ctx.stroke();
      ctx.closePath();
    } else if (this.type === 'explosive') {
      // Add an 'X' pattern for explosive bricks
      ctx.beginPath();
      ctx.moveTo(this.x, this.y);
      ctx.lineTo(this.x + this.width, this.y + this.height);
      ctx.moveTo(this.x + this.width, this.y);
      ctx.lineTo(this.x, this.y + this.height);
      ctx.strokeStyle = '#FFFFFF';
      ctx.lineWidth = 2;
      ctx.stroke();
      ctx.closePath();
    } else if (this.type === 'unbreakable') {
      // Add a cross-hatch pattern for unbreakable bricks
      ctx.beginPath();
      for (let i = 0; i < this.width; i += 8) {
        ctx.moveTo(this.x + i, this.y);
        ctx.lineTo(this.x + i, this.y + this.height);
        ctx.moveTo(this.x, this.y + i);
        ctx.lineTo(this.x + this.width, this.y + i);
      }
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 0.5;
      ctx.stroke();
      ctx.closePath();
    }
  }
}

export default Brick;
