class Paddle {
  constructor(x, y) {
    this.initialX = x;
    this.initialY = y;
    this.x = x;
    this.y = y;
    this.width = 100;
    this.height = 10;
    this.speed = 8;
    this.dx = 0;
    this.leftPressed = false;
    this.rightPressed = false;
    this.setupControls();
  }

  setupControls() {
    document.addEventListener('keydown', (e) => this.handleKeyDown(e));
    document.addEventListener('keyup', (e) => this.handleKeyUp(e));
    document.addEventListener('touchstart', (e) => this.handleTouchStart(e));
    document.addEventListener('touchmove', (e) => this.handleTouchMove(e));
    document.addEventListener('touchend', () => this.handleTouchEnd());
  }

  cleanup() {
    document.removeEventListener('keydown', (e) => this.handleKeyDown(e));
    document.removeEventListener('keyup', (e) => this.handleKeyUp(e));
    document.removeEventListener('touchstart', (e) => this.handleTouchStart(e));
    document.removeEventListener('touchmove', (e) => this.handleTouchMove(e));
    document.removeEventListener('touchend', () => this.handleTouchEnd());
  }

  handleKeyDown(e) {
    if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') {
      this.leftPressed = true;
    }
    if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') {
      this.rightPressed = true;
    }
  }

  handleKeyUp(e) {
    if (e.key === 'ArrowLeft' || e.key === 'a' || e.key === 'A') {
      this.leftPressed = false;
    }
    if (e.key === 'ArrowRight' || e.key === 'd' || e.key === 'D') {
      this.rightPressed = false;
    }
  }

  handleTouchStart(e) {
    e.preventDefault();
    const touch = e.touches[0];
    this.touchX = touch.clientX;
  }

  handleTouchMove(e) {
    e.preventDefault();
    const touch = e.touches[0];
    const deltaX = touch.clientX - this.touchX;
    this.x = Math.max(0, Math.min(800 - this.width, this.x + deltaX));
    this.touchX = touch.clientX;
  }

  handleTouchEnd() {
    this.touchX = null;
  }

  update() {
    if (this.leftPressed) {
      this.dx = -this.speed;
    } else if (this.rightPressed) {
      this.dx = this.speed;
    } else {
      this.dx = 0;
    }

    this.x += this.dx;

    // Keep paddle within bounds
    if (this.x < 0) {
      this.x = 0;
    } else if (this.x + this.width > 800) {
      this.x = 800 - this.width;
    }
  }

  reset() {
    this.x = this.initialX;
    this.y = this.initialY;
    this.dx = 0;
    this.leftPressed = false;
    this.rightPressed = false;
  }

  draw(ctx) {
    ctx.beginPath();
    ctx.rect(this.x, this.y, this.width, this.height);
    ctx.fillStyle = '#0095DD';
    ctx.fill();
    ctx.closePath();
  }
}

export default Paddle;
