class Ball {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.radius = 8;
    this.baseSpeed = 1000; // Much faster base speed
    this.speed = this.baseSpeed;
    this.dx = 0;
    this.dy = 0;
    this.isLaunched = false;
    this.canvasWidth = 800;
    this.canvasHeight = 600;
  }

  setSpeed(multiplier) {
    this.speed = this.baseSpeed * multiplier;
  }

  launch() {
    if (!this.isLaunched) {
      this.isLaunched = true;
      // Launch at -60 degrees (upward angle)
      const angle = -Math.PI / 3;
      this.dx = this.speed * Math.cos(angle);
      this.dy = this.speed * Math.sin(angle);
    }
  }

  update(deltaTime) {
    if (this.isLaunched) {
      // Update position
      this.x += this.dx * deltaTime;
      this.y += this.dy * deltaTime;

      // Bounce off walls
      if (this.x - this.radius <= 0) {
        this.x = this.radius;
        this.dx = Math.abs(this.dx);
      } else if (this.x + this.radius >= this.canvasWidth) {
        this.x = this.canvasWidth - this.radius;
        this.dx = -Math.abs(this.dx);
      }

      if (this.y - this.radius <= 0) {
        this.y = this.radius;
        this.dy = Math.abs(this.dy);
      }
    }
  }

  draw(ctx) {
    ctx.beginPath();
    ctx.arc(Math.round(this.x), Math.round(this.y), this.radius, 0, Math.PI * 2);
    ctx.fillStyle = '#00FF00';
    ctx.fill();
    ctx.closePath();
  }

  reset() {
    this.isLaunched = false;
    this.dx = 0;
    this.dy = 0;
  }

  stickToPaddle(paddle) {
    this.x = paddle.x + paddle.width / 2;
    this.y = paddle.y - this.radius;
  }

  checkPaddleCollision(paddle) {
    if (!this.isLaunched) return false;

    return (this.x + this.radius > paddle.x &&
            this.x - this.radius < paddle.x + paddle.width &&
            this.y + this.radius > paddle.y &&
            this.y - this.radius < paddle.y + paddle.height);
  }

  bounceOffPaddle(paddle) {
    // Calculate hit position relative to paddle center (-1 to 1)
    const hitX = (this.x - (paddle.x + paddle.width / 2)) / (paddle.width / 2);

    // Calculate bounce angle (-60 to 60 degrees)
    const maxAngle = Math.PI / 3; // 60 degrees
    const angle = hitX * maxAngle;

    // Set new velocity maintaining speed
    this.dy = -Math.abs(this.speed * Math.cos(angle));
    this.dx = this.speed * Math.sin(angle);

    // Ensure ball is above paddle
    this.y = paddle.y - this.radius;
  }

  checkBrickCollision(brick) {
    if (!this.isLaunched || brick.isDestroyed()) return false;

    return (this.x + this.radius > brick.x &&
            this.x - this.radius < brick.x + brick.width &&
            this.y + this.radius > brick.y &&
            this.y - this.radius < brick.y + brick.height);
  }

  bounceOffBrick(brick) {
    // Calculate collision point relative to brick center
    const relX = this.x - (brick.x + brick.width / 2);
    const relY = this.y - (brick.y + brick.height / 2);

    // Determine if collision is more horizontal or vertical
    if (Math.abs(relX) * brick.height > Math.abs(relY) * brick.width) {
      // Horizontal collision
      this.dx = -this.dx;
    } else {
      // Vertical collision
      this.dy = -this.dy;
    }
  }
}

export default Ball;
